import { Component, OnInit } from '@angular/core';
import { StudentService } from 'src/app/services/student.service';

@Component({
  selector: 'app-all-student',
  templateUrl: './all-student.component.html',
  styleUrls: ['./all-student.component.css']
})
export class AllStudentComponent implements OnInit {
  constructor(private studentService: StudentService) { }
  students: any[] = [];

  ngOnInit(): void {

    this.getAllStudent();
  }

  getAllStudent() {
    this.studentService.allStudent().subscribe((res) => {
      this.students = res;
      if (this.students.length == 0) {
        alert("No student found");
      }
    });
  }

  deleteStudent(id : any){

    this.studentService.deleteStudent(id).subscribe((res) => {
      alert(res);
      this.getAllStudent();
    });
  }

}
<app-faculty-menu></app-faculty-menu>

<!DOCTYPE html>
<html lang="en">
<head>
  <meta charset="UTF-8">
  <meta name="viewport" content="width=device-width, initial-scale=1.0">
  <title>All Students</title>
</head>
<body>
  <div class="table-container">
    <div class="card">
      <div class="card-header text-center">
        <h2>Student List</h2>
      </div>
      <div class="card-body">
        <table class="table table-hover table-bordered table-striped">
          <thead class="table-dark">
            <tr>
              <th>#</th>
              <th>Name</th>
              <th>Email</th>
              <th>Action</th>
            </tr>
          </thead>
          <tbody>
            <tr *ngFor="let student of students; let i = index">
              <td>{{ i + 1 }}</td>
              <td>{{ student.name }}</td>
              <td>{{ student.email }}</td>
              <td>
                <a [routerLink]="['/edit-student',student.id]" class="btn btn-sm btn-success me-2">Edit</a>
                <button class="btn btn-sm btn-danger" (click)="deleteStudent(student.id)">Delete</button>
              </td>
            </tr>
          </tbody>
        </table>
      </div>
    </div>
  </div>

</body>
</html>
