import { Component, OnInit } from '@angular/core';
import { SubjectService } from 'src/app/services/subject.service';

@Component({
  selector: 'app-all-subject',
  templateUrl: './all-subject.component.html',
  styleUrls: ['./all-subject.component.css']
})
export class AllSubjectComponent implements OnInit {

  constructor(private subjectService: SubjectService) { }

  subjectList: any[] = [];

  ngOnInit(): void {
    this.allSubjects();
  }

  allSubjects() {
    this.subjectService.allSubjects().subscribe((res) => {
      this.subjectList = res;
    })
  }



}
<app-admin-menu></app-admin-menu>

<!DOCTYPE html>
<html lang="en">
<head>
  <meta charset="UTF-8">
  <meta name="viewport" content="width=device-width, initial-scale=1.0">
  <title>All Subject</title>
</head>
<body>


<div class="container mt-4">
  <h2 class="text-center page-title">All Subject</h2>

  <div class="table-responsive">
    <table class="table table-bordered table-striped table-hover">
      <thead class="table-dark">
        <tr>
          <th>ID</th>
          <th>Name</th>
          <th>ACTION</th>
        </tr>
      </thead>

      <tbody>
        <tr *ngFor="let subject of subjectList">
          <td>{{ subject.id }}</td>
          <td>{{ subject.name}}</td>

          <td>

            <a class="btn btn-success" [routerLink]="['/edit-subject', subject.id]">Edit</a>

          </td>
        </tr>
      </tbody>
    </table>
  </div>
</div>

</body>
</html>
