import { Component, OnInit } from '@angular/core';
import { SubjectService } from 'src/app/services/subject.service';
import { ActivatedRoute } from '@angular/router';

@Component({
  selector: 'app-edit-subject',
  templateUrl: './edit-subject.component.html',
  styleUrls: ['./edit-subject.component.css']
})
export class EditSubjectComponent {
updateSubject() {
throw new Error('Method not implemented.');
}

  subject: any;

  constructor(private subjectService: SubjectService, private activatedRoute: ActivatedRoute) {
    let id = activatedRoute.snapshot.paramMap.get('subjectid');
    this.getSubject(id)
  }


  getSubject(id: any) {
    this.subjectService.getSubject(id).subscribe((res) => {
      this.subject=res;
    })
  }



}

<app-admin-menu></app-admin-menu>

<!DOCTYPE html>
<html lang="en">
<head>
  <meta charset="UTF-8">
  <meta name="viewport" content="width=device-width, initial-scale=1.0">
  <title>Add Subject</title>
</head>
<body>
  <div class="add-user-wrapper">
    <div class="container">
      <h2 class="form-title text-center">Update Subject</h2>

      <form>

        <div class="mb-3">
          <label for="subjectId" class="form-label">Subject Id</label>
          <input type="text" class="form-control" id="subjectId" placeholder="Enter subject Id" name="subjectId" [(ngModel)]="subject.id" readonly>
        </div>

        <div class="mb-3">
          <label for="subjectName" class="form-label">Subject Name</label>
          <input type="text" class="form-control" id="subjectName" placeholder="Enter subject name" name="subjectName" [(ngModel)]="subject.name" >
        </div>

        <div class="text-center">
          <button type="submit" (click)="updateSubject()" class="btn btn-primary w-100">Update Subject</button>
        </div>
      </form>
    </div>
  </div>
</body>
</html>
