import { Component, OnInit } from '@angular/core';
import { AttendanceService } from 'src/app/services/attendance.service';
import { SubjectService } from 'src/app/services/subject.service';
import { UserService } from 'src/app/services/user.service';

@Component({
  selector: 'app-view-all-attendance',
  templateUrl: './view-all-attendance.component.html',
  styleUrls: ['./view-all-attendance.component.css']
})
export class ViewAllAttendanceComponent implements OnInit {

  constructor(
    private subjectService: SubjectService,
    private userService: UserService,
    private attendanceService: AttendanceService
  ) { }

  subjects: any[] = [];
  users: any[] = [];
  attendanceRecords: any[] = [];

  selectedUser: string = '';
  selectedSubject: any;
  selectedDate: string = '';

  ngOnInit(): void {
    this.getSubjects();
    this.getUsers();
  }

  getSubjects() {
    this.subjectService.allSubjects().subscribe((res) => {
      this.subjects = res;
    });
  }

  getUsers() {
    this.userService.getAllFaculty().subscribe((res) => {
      this.users = res;
    })
  }

  changeUser(event: any) {
    this.selectedUser = event.target.value;

  }
  changeSubject(event: any) {
    this.selectedSubject = event.target.value;
  }

  changeDate(event: any) {
    this.selectedDate = event.target.value;
  }

  selectedStudents: any[] = [];
  isModalOpen: boolean = false;
  showStudents(students: any[]) {
    this.selectedStudents = students;
    this.isModalOpen = true;
    console.log(this.selectedStudents);

  }

  closeModal() {
    this.isModalOpen = false;
  }

  fetchAttendanceRecords() {
    this.attendanceService.filteredAttendance(this.selectedUser, this.selectedSubject, this.selectedDate).subscribe((res) => {
      this.attendanceRecords = res;
      if (this.attendanceRecords.length === 0) {
        alert("No attendance records found for the selected date");
      }
    })
  }

  fetchAllAttendanceRecords() {
    this.attendanceService.allAttendance().subscribe((res) => {
      this.attendanceRecords = res;
      if (this.attendanceRecords.length === 0) {
        alert("No attendance records found for the selected date");
      }
    })
  }
}
<app-admin-menu></app-admin-menu>

<!DOCTYPE html>
<html lang="en">

<head>
  <meta charset="UTF-8">
  <meta name="viewport" content="width=device-width, initial-scale=1.0">
  <title>View Attendance</title>

</head>

<body>
  <div class="container">
    <!-- Filter Row -->
    <div class="row mb-3">
      <div class="col-md-3">
        <label for="faculty" class="form-label">Select Faculty</label>
        <select id="faculty" class="form-select" (change)="changeUser($event)">
          <option value="">Choose Faculty</option>
          <!-- Populate dynamically -->
          <option *ngFor="let user of users" [value]="user.username">{{ user.firstName }} {{ user.lastName }}</option>
        </select>
      </div>
      <div class="col-md-3">
        <label for="subject" class="form-label">Select Subject</label>
        <select id="subject" class="form-select" (change)="changeSubject($event)">
          <option value="">Choose Subject</option>
          <!-- Populate dynamically -->
          <option *ngFor="let subject of subjects" [value]="subject.id">{{ subject.name }}</option>
        </select>
      </div>
      <div class="col-md-3">
        <label for="date" class="form-label">Select Date</label>
        <input type="date" id="date" class="form-control" (change)="changeDate($event)">
      </div>

      <div class="col-md-3 text-center">
        <button class="btn btn-success w-100 mb-1" (click)="fetchAllAttendanceRecords()">Show All</button>
        <button class="btn btn-primary w-100" (click)="fetchAttendanceRecords()">Show</button>
      </div>
    </div>

    <!-- Attendance Table -->
    <div class="row">
      <div class="col">
        <table class="table table-bordered table-striped">
          <thead class="table-dark">
            <tr>
              <th>#</th>
              <th>Faculty</th>
              <th>Subject</th>
              <th>Date</th>
              <th>Time</th>
              <th>Number of Students</th>
              <th>Show Students</th>
            </tr>
          </thead>
          <tbody id="attendanceTableBody">
            <!-- Data will be inserted dynamically -->
            <tr *ngFor="let record of attendanceRecords; let i = index">
                <td>{{ i + 1 }}</td>
              <td>{{ record.user.firstName | titlecase }} {{ record.user.lastName | titlecase }}</td>
              <td>{{ record.subject.name | titlecase }}</td>
              <td>{{ record.date | date }}</td>
              <td>{{ record.time }}</td>
              <td>{{ record.numberOfStudents }}</td>
              <td>
                <button class="btn btn-info" (click)="showStudents(record.students)" data-bs-toggle="modal" data-bs-target="#studentModal">
                  Show
                </button>
              </td>
            </tr>
          </tbody>
        </table>
      </div>
    </div>
  </div>




  <!-- Modal (Visible only when isModalOpen is true) -->
<div *ngIf="isModalOpen" class="modal-overlay">
  <div class="modal-content">
    <div class="modal-header">
      <h5 class="modal-title">Student List</h5>
      <button class="close-btn" (click)="closeModal()">×</button>
    </div>
    <div class="modal-body">
      <div class="row">
        <div class="col-md-3 mb-3" *ngFor="let student of selectedStudents">
          <div class="student-card p-2 border rounded text-center">
            {{ student.name }}
          </div>
        </div>
      </div>
    </div>
  </div>
</div>


</body>

</html>
