import { Component } from '@angular/core';
import { Router } from '@angular/router';
import { UserService } from 'src/app/services/user.service';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css'],
})
export class LoginComponent {

  constructor(private userService: UserService, private router: Router) { }

  user = {
    username: '',
    password: '',
  };

  login() {
    this.userService.login(this.user).subscribe((res) => {
      console.log(res);

      if (res != null) {

        localStorage.setItem('user', res.username);
        localStorage.setItem('role',res.role);

        if (res.role === 'admin') {
          this.router.navigateByUrl('admin-dashboard');
        } else {
          this.router.navigateByUrl('faculty-dashboard');
        }

      } else {
        alert("Invalid Credientials !!")
      }
    })
  }
}
<!DOCTYPE html>
<html lang="en">
<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title>Login - Student Attendance</title>
</head>
<body>
    <div class="content">
        <h1>Login</h1>
        <form>
            <div class="mb-3">
                <label for="text" class="form-label">UserName</label>
                <input type="text" class="form-control" id="text" placeholder="Enter your username" name="username" [(ngModel)]="user.username">
            </div>
            <div class="mb-3">
                <label for="password" class="form-label">Password</label>
                <input type="password" class="form-control" id="password" placeholder="Enter your password" name="password" [(ngModel)]="user.password">
            </div>
            <div class="mb-3 form-check">
                <input type="checkbox" class="form-check-input" id="rememberMe">
                <label class="form-check-label" for="rememberMe">Remember Me</label>
            </div>
            <button type="submit" (click)= "login()" class="btn btn-primary">Login</button>
        </form>
        <p class="text-center mt-3"><a href="#">Forgot Password?</a></p>
        <p class="text-center">Don't have an account? <a href="#">Sign Up</a></p>
        </div>
</body>
</html>
